import React from "react";
import { ReactSketchCanvas } from "react-sketch-canvas";

const styles = {
  border: "0.0625rem solid #9c9c9c",
  borderRadius: "0.25rem"
};

const Canvas = class extends React.Component {
  constructor(props) {
    super(props);

    this.canvas = React.createRef();
  }

  render() {
    return (
      <div>
        <ReactSketchCanvas
          ref={this.canvas}
          strokeWidth={5}
          strokeColor="black"

        />
        <button
          onClick={() => {
            this.canvas.current.exportSvg()
              .then(data => {
                this.canvas.current.exportImage('svg').then(data => { console.log(data) })
                console.log(data);
                data.download();
              }).catch(e => {
                console.log(e)
              })
          }}
        >
          Get Image
        </button>
        <button
          onClick={() => {
            this.canvas.current.clearCanvas()
          }}
        >
          Clear Image
        </button>
      </div>
    );
  }
};

export default Canvas;
